import React, { cloneElement } from "react";
import {
  StyleSheet,
  View,
  Image,
  TextInput,
  TouchableOpacity,
  Text,
} from "react-native";

import Header from "../components/Header";
import TopNavigation from "../components/TopNavigation";

const BLUE = "#2C98F0";
const GRAY = ("#ffffff", 42);

const Login = (props) => {
  return (
    <View style={styles.container}>
      <View>
        <Header title={props.title} />
        <TopNavigation />
      </View>
      <Image
        source={require("../assets/client-logo.png")}
        style={styles.logo}
      />
      <View>
        <TextInput
          nativeID="user"
          textAlign="left"
          placeholder="Email/Phone"
          sectionColor={"#2C98F0"}
          underlineColorAndroid={GRAY}
          style={styles.user}
          onChangeText={props.enterEmail}
          value={props.loginEmail}
        />
        <TextInput
          nativeID="password"
          textAlign="left"
          placeholder="Password"
          sectionColor={"#2C98F0"}
          underlineColorAndroid={GRAY}
          style={styles.passsword}
        />
      </View>
      <TouchableOpacity style={styles.login} onPress={props.logIn}>
        <Text style={styles.title}>LOGIN</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    //flex: 1,
    backgroundColor: "#fff",
    justifyContent: "center",
  },

  logo: {
    width: 244,
    height: 60,
    alignSelf: "center",
    top: 80,
  },

  user: {
    width: 328,
    height: 45,
    alignSelf: "center",
    top: 100,
    fontSize: 16,
    fontFamily: "Roboto",
    paddingLeft: 6,
  },

  passsword: {
    width: 328,
    height: 45,
    alignSelf: "center",
    top: 140,
    fontSize: 16,
    fontFamily: "Roboto",
    paddingLeft: 6,
  },

  login: {
    top: 190,
    alignItems: "center",
    backgroundColor: "#23395D",
    width: 320,
    height: 53,
    alignSelf: "center",
  },

  title: {
    fontFamily: "Roboto",
    fontWeight: "bold",
    fontSize: 16,
    color: "white",
    alignSelf: "center",
    top: 16,
  },
});

export default Login;
